import { Component, OnInit } from '@angular/core';
import { OrderService } from '../core/services/order.service';
import { Order, OrderMasterData } from '../core/model/order.model';
// import{ Constants} from '../core/constants/constants';
@Component({
  selector: 'app-orders',
  templateUrl: './orders.component.html',
  styleUrls: ['./orders.component.scss']
})
export class OrdersComponent implements OnInit {
  isModalOpen = false;
  modalOrder:any;// dont use any 
  url="../../assets/images/download.jpeg"; //keep in constants
  orders:Order[]=[];
  displayedOrders:Order[]=[];
  itemsPerPage:number =4;
  constructor(private orderService: OrderService){}
  ngOnInit():void 
  {
    this.getOrders();
  }
  /**
   * This method is used to get load more orders 
   */

  loadMore() : void{
    const startIndex = this.displayedOrders.length;
    const endIndex = startIndex + this.itemsPerPage;
    if (startIndex < this.orders.length) {
      this.displayedOrders = this.displayedOrders.concat(this.orders.slice(startIndex, endIndex));
    }
  }
  /**
   * 
   * @param order Takes a order as a parameter when the user clicks the view details 
   */
  viewDetails(order:Order) : void  {
    console.log(order);
    this.openModal();
    this.modalOrder=order;
  }
  /**
   * This method is called when the user clicks on the view details button which triggers the modal behaviour 
   */
  openModal():void {
    this.isModalOpen=true;
  }
  /**
   * This method is called when the user clicks on the view details button which triggers the modal behaviour and closes the modal 
   */
  closeModal():void{
    this.isModalOpen=false;
  }
  /**
   * This method is used to get the order details from the api using the order service .
   */
  getOrders():void {
    this.orderService.getOrders().subscribe((response:OrderMasterData) => {
      this.orders = response.orders;
      this.loadMore();
    });
  }
}
<div class="container">
  <div class="order-container">
    <h3 class="page-title">Your Orders</h3>
    <div class="order-cell" *ngFor="let order of displayedOrders">
      <div class="order-details">
        <img class="order-image" [src]="url" alt="food" />
        <div class="order-tab">
          <h3 class="food-title">{{ order.restaurant }}</h3>
          <p class="order-place">{{ order.location }}</p>
          <p class="order-id">Order #{{ order.id }}</p>
          <div class="order-items">
            <p class="food-item" *ngFor="let item of order.orderItems">
              {{ item.name }} x {{ item.quantity}}
            </p>
          </div>
        </div>
      </div>
      <div class="order-status">
        <div class="order-info">
          <p *ngIf="order.status=='ORDERED'">Ordered on {{ order.orderDateTime | dateCustom }}</p>
          <p *ngIf="order.status=='DELIVERED'">Delivered  on {{ order.orderDateTime | dateCustom }}</p>
          <p class="order-price">Total: {{ order.price?.subTotal }}</p>
        </div>
        <div class="order-btn-section">
          <button class="btn-danger" type="button" (click)="viewDetails(order)">
            View Details
          </button>
        </div>
      </div>
    </div>
    <button
      class="show-more btn-danger"
      type="button"
      (click)="loadMore()"
      *ngIf="displayedOrders.length < orders.length"
    >
      show more
    </button>
  </div>
  <div class="modal" *ngIf="isModalOpen">
    <div class="view-order">
      <div class="modal-container">
            <img class="modal-image" [src]="url" alt="food" />
            <div class="modal-detail">
                <p class="order-place">Restaurant Name:{{ modalOrder.restaurant }}</p>
                <p class="order-place">Order Status :{{ modalOrder.status }}</p>
                <p class="order-place">Payment Details: {{ modalOrder.payment}} </p>
                <div class="food-item" *ngFor="let item of modalOrder.orderItems">
                  <p>Food : {{ item.name }}</p>
                  <p>Quantity: {{ item.quantity }}</p>
                  <p>Cost:{{ item.costForOne*item.quantity }}</p>
                </div>
            </div>
      </div>
      <button type="button" class="btn-danger" (click)="closeModal()">
        Close
      </button>
    </div>
  </div>
</div>
