import { Component, OnInit } from '@angular/core';
import { OrderService } from '../core/services/order.service';
import { Order } from '../core/model/order.model';

@Component({
  selector: 'app-orders',
  templateUrl: './orders.component.html',
  styleUrls: ['./orders.component.scss']
})
export class OrdersComponent implements OnInit {
  isModalOpen = false;
  viewOrder?:Order;
  url="../../assets/images/download.jpeg";
  orders:Order[]=[];
  displayedOrders:Order[] = [];
  itemsPerPage:number =4;
  constructor(private orderService: OrderService){}
  ngOnInit() 
  {
    this.orderService.getOrders().subscribe(response => {
      this.orders = response.orders.map((order:Order) => order);
      this.loadMore();
    });

  }
  loadMore() {
    const startIndex = this.displayedOrders.length;
    const endIndex = startIndex + this.itemsPerPage;
    if (startIndex < this.orders.length) {
      this.displayedOrders = this.displayedOrders.concat(this.orders.slice(startIndex, endIndex));
    }
  }
  viewDetails(order:Order) {
    console.log(order);
    this.openModel();
    this.viewOrder=order;
  }
  openModel(){
    this.isModalOpen=true;
  }
  closeModel() {
    this.isModalOpen=false;
  }
}
<div class="container">
    <div class="order-container" >
        <h3 class="order-title">Your Orders</h3>
        <div class="order-cell" *ngFor="let order of displayedOrders">
            <div class="order-details">
                <img class="order-image" [src]='url' alt="food">
                <div class="order-tab">
                    <h3 class="food-title">{{order.restaurant}}</h3>
                    <p class="order-place">{{order.location}}</p>
                    <p class="order-id">Order #{{order.id}}</p>
                    <div class="order-items">
                        <p class="food-item" *ngFor="let item of order.orderItems">{{item.name}}</p>

                    </div>
                </div>
            </div>
            <div class="order-status">
                <div class="order-info">
                    <p>Ordered on {{ order.orderDateTime | dateCustom }} </p>
                    <p class="order-price">Total: {{ order.price?.subTotal}}</p>
                </div>
                <div class="order-btn-section">
                    <button class="btn-danger" type="button" (click)="viewDetails(order)">View Details</button>
                </div>
            </div>
        </div>
        <button class="show-more btn-danger" type="button" (click)="loadMore()" *ngIf="displayedOrders.length < orders.length">show more</button>
    </div>
    <div class="modal" *ngIf="isModalOpen">
        <div class="modal-container">

        </div>
    </div>
</div>
