import { Component } from '@angular/core';
import { OfferService } from '../core/services/offer.service';
import { Offer, OfferMasterData } from '../core/model/offer.model';

@Component({
  selector: 'app-offers',
  templateUrl: './offers.component.html',
  styleUrls: ['./offers.component.scss']
})
export class OffersComponent {
offerImageUrl='../../assets/images/chicken-breast-pork-chop-with-beef-meat-steak-vegetable.jpg';
offers:any=[];
constructor(private offerService: OfferService){}
ngOnInit(){
  this.getOrders();
}
getOrders():void {
  this.offerService.getOffers().subscribe((response:OfferMasterData) => {
    this.offers = response.result;
    console.log(this.offers)
  });
}
}
<div class="container">
  <div class="offer-component">
    <div class="page-title">Offers</div>
    <div class="offer-container">
      <div class="offer-cell" *ngFor="let offer of offers">
        <img class="food-image" [src]="offerImageUrl" alt="" />
        <div class="offer-details">
          <div class="food-option"></div>
          <div class="food-details">
            <div class="food-name">Ham sandwich{{ offers.name }}</div>
            <div class="restaurant-name">Cafe Coffee Day,Mount Road{{ offers.restaurant }}</div>
            <div class="delivery-detail">Delivery in 37 mins{{ offers.deliveryTimeInMins }}</div>
          </div>
          <div class="price-details">
            <div class="new-price">$10.50{{ offers.price.discountPrice }}</div>
            <div class="old-price">$18.50{{ offer.price.costPrice }}</div>
          </div>
        </div>
        <button class="btn-danger" type="button">Add to Cart</button>
      </div>
    </div>
  </div>
</div>
